  //     }
  //   } catch (error) {
  //     console.error("Error fetching weather data:", error);
  //     this.setState({ errorMsg: "Error fetching weather data." });
  //   }
  // };

   getWeather = async (lat, lon) => {
    try {
      const apiKey = 'd9483472d1b24148d8a4c7673484ea585'; // Replace with your actual API key
      const apiUrl = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&units=metric&appid=${apiKey}`;

      const response = await fetch(apiUrl);

      if (!response.ok) {
        const errorData = await response.json();
        console.error('Weather data not found:', errorData);
        throw new Error(`Error: ${errorData.message} (Code: ${errorData.cod})`);
      }

      const data = await response.json();
      // Process your weather data here, e.g., update state or display it
      console.log(data);
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  };


  setWeatherIcon(main) {
    switch (main) {
      case "Haze":
        this.setState({ icon: "CLEAR_DAY" });
        break;
      case "Clouds":
        this.setState({ icon: "CLOUDY" });
        break;
      case "Rain":
        this.setState({ icon: "RAIN" });
        break;
      case "Snow":
        this.setState({ icon: "SNOW" });
        break;
      case "Dust":
      case "Tornado":
        this.setState({ icon: "WIND" });
        break;
      case "Drizzle":
        this.setState({ icon: "SLEET" });
        break;
      case "Fog":
      case "Smoke":
        this.setState({ icon: "FOG" });
        break;
      default:
        this.setState({ icon: "CLEAR_DAY" });
    }
  }

  render() {
    if (this.state.errorMsg) {
      return <h3 style={{ color: "red" }}>{this.state.errorMsg}</h3>;
    }

    if (this.state.temperatureC) {
      return (
        <React.Fragment>
          <div className="city">
            <div className="title">
              <h2>{this.state.city}</h2>
              <h3>{this.state.country}</h3>
            </div>
            <div className="mb-icon">
              <ReactAnimatedWeather
                icon={this.state.icon}
                color={defaults.color}
                size={defaults.size}
                animate={defaults.animate}
              />
              <p>{this.state.main}</p>
            </div>
            <div className="date-time">
              <div className="dmy">
                <div id="txt"></div>
                <div className="current-time">
                  <Clock format="HH:mm:ss" interval={1000} ticking={true} />
                </div>
                <div className="current-date">{dateBuilder(new Date())}</div>
              </div>
              <div className="temperature">
                <p>
                  {this.state.temperatureC}Â°<span>C</span>
                </p>
              </div>
            </div>
          </div>
          <Forcast icon={this.state.icon} weather={this.state.main} />
        </React.Fragment>
      );
    } else {
      return (
        <React.Fragment>
          <img src={loader} style={{ width: "50%", WebkitUserDrag: "none" }} />
          <h3 style={{ color: "white", fontSize: "22px", fontWeight: "600" }}>
